<br />
//to clear if already any nodes were selected<br />
<pre><code>            selected_nodes.Clear();</code></pre>

//to make all nodes background color as black in default<br />
<pre><code>            foreach (PocVertex vertex in graphLayout.Graph.Vertices)
            {
                vertex.Color_val = &quot;Black&quot;;

            }

        double x = Canvas.GetLeft(dragSelectionBorder);
        double y = Canvas.GetTop(dragSelectionBorder);

        double width = dragSelectionBorder.Width;
        double height = dragSelectionBorder.Height;
        Rect dragRect = new Rect(x, y, width, height);
</code></pre>

// creating an item rectangle for each node,and to check whether any nodes is under the selection rectangle dragrect<br />
<pre><code>        foreach (PocVertex vertex in graphLayout.Graph.Vertices)
        {
            VertexControl vs;
            double vertexX=0;
            double vertexY=0;
            vs = graphLayout.GetVertexControl(vertex);
            Point relativePoint = vs.TransformToAncestor(this).Transform(new Point(0, 0));
            vertexX = relativePoint.X;
            vertexY = relativePoint.Y;
            Rect itemrect = new Rect(vertexX,vertexY, vs.ActualWidth, vs.ActualHeight);
</code></pre>

//checking itemrectangle (of node) is inside selection rectangle dragRect or not<br />
<pre><code>            if (dragRect.Contains(itemrect))
            {

                selected_nodes.Add(vertex.ID.ToString());</code></pre>

//changing Background of node this particular node which is inside the selection rectangle dragRect  from black to red to identify the selected nodes<br />
<pre><code>                vertex.Color_val = &quot;red&quot;;
            } 
        }

</code></pre>

