//the pocvertex.cs file <br />
<br />
public class PocVertex:INotifyPropertyChanged<br />
<pre><code>{
    public string ID { get;  set; }
    public string colorval=&quot;Black&quot;;
    public string Color_val { 
        get{
            return colorval;
                }
        set
        {
            colorval = value;
            NotifyPropertyChanged(&quot;Color_val&quot;);
        }
    }
</code></pre>

#region INotifyPropertyChanged Implementation<br />
<pre><code>    public event PropertyChangedEventHandler PropertyChanged;

    private void NotifyPropertyChanged(string info)
    {
        if (PropertyChanged != null)
        {
            PropertyChanged(this, new PropertyChangedEventArgs(info));
        }
    }

    #endregion

</code></pre>

// Vertex Control modification in xml side<br />
<br />
  &lt;Style TargetType=&quot;{x:Type graphsharp:VertexControl}&quot; x:Name=&quot;Vertex_Control&quot; &gt;<br />
<pre><code>        &lt;Setter Property=&quot;Template&quot;&gt;
            &lt;Setter.Value&gt;
                &lt;ControlTemplate TargetType=&quot;{x:Type graphsharp:VertexControl}&quot; x:Name=&quot;Vertex_Control_Control_Template&quot;&gt;

                        &lt;Border x:Name=&quot;Vertex_Border&quot;
                               BorderBrush=&quot;White&quot;
                                BorderThickness=&quot;2&quot; Background=&quot;{Binding Color_val,UpdateSourceTrigger=PropertyChanged}&quot;
                                CornerRadius=&quot;10,10,10,10&quot;  
                                Padding=&quot;{TemplateBinding Padding}&quot;&gt;

                            &lt;StackPanel Orientation=&quot;Vertical&quot; x:Name=&quot;Vertex_Stack_Panel&quot; &gt;
                            &lt;ContentPresenter Content=&quot;{TemplateBinding Vertex}&quot; 
                                       ContentTemplate=&quot;{StaticResource demoTemplate}&quot; /&gt;

                            &lt;/StackPanel&gt;

                    &lt;/Border&gt;


                &lt;/ControlTemplate&gt;

            &lt;/Setter.Value&gt;
        &lt;/Setter&gt;


    &lt;/Style&gt;

</code></pre>
